import './trails.css';
import Axios from "axios";
import { useState } from "react";
import RecipeTile from "./RecipeTile";


function Trails() {
  const [query, setquery] = useState("");  
  //const [recipes, setrecipes] =useState([]);
  //const [healthLabel, setHealthLabel] = useState("");

  const [trails, setTrails] = useState([]);


  //const YOUR_APP_ID = "eff0de92";
  //const YOUR_APP_KEY = "52c8e7fde509c5c4403dface7fe6c1d7";
  //var url = `https://api.edamam.com/search?q=${query}&app_id=${YOUR_APP_ID}&app_key=${YOUR_APP_KEY}`;


  const options = {
    method: 'GET',
    url: 'https://trailapi-trailapi.p.rapidapi.com/trails/map/juliep/gpx/',
    headers: {
      'x-rapidapi-key': 'c51c181cabmsh4a4771070bc1dbdp19f4dcjsn176b0b9e7dae',
      'x-rapidapi-host': 'trailapi-trailapi.p.rapidapi.com'
    }
  };

  Axios.request(options).then(function (response) {
    console.log(response.data);
  }).catch(function (error) {
    console.error(error);
  });

  async function getTrails(){
    var result = await Axios.get(options);
    setTrails(result.data.hits);
    console.log(result.data);

  };


  const onSubmit = (e) => {
    e.preventDefault();
    getTrails();

  }

  return (
    <div className = "app">
      <h1 onClick ={getTrails}>Trails Plaza </h1>
      <form className="app_searchForm" onSubmit={onSubmit}>
        <input 
          type="text" 
          className="app__input"
          placeholder="enter ingredient" 
          value={query} 
          onChange={(e) => setquery(e.target.value)}
        />
        <input className="app__submit" type="submit" value="Search" />

      </form>

      <div className="app__recipes">
        {recipes.map((recipe) => {
          return <RecipeTile recipe={recipe}></RecipeTile>;
        })}
      </div>
    </div>
  );
}

export default Trails;
