import './login.css';
import Register from './register';


function Login() {
  return (

    <div className="login-register-container">
        <div className="text-center">
            <h2 className="login-register-header">Sign in</h2>
        </div>

      <form className="login-register-form">

        <div className="form-group row">
          <label className="col-sm-3 col-form-label" >Username</label>
            <div className="col-sm-9">
              <input className="form-control-login" type="text" placeholder="Username"/>
            </div>
        </div> 

        <div className="form-group row">
          <label className="col-sm-3 col-form-label" >Password</label>
            <div className="col-sm-9">
              <input className="form-control-login" type="text" placeholder="Password"/>
            </div>   
        </div> 
        <div>     
              <input type="submit" className="login-btn" value="login"/>
        </div>
      </form>

      <div className ="new-expense">
            {!isRegistering && <button onClick={startRegistrationHandler}>Add New Expense</button>}
            {isRegistering && <Register onSaveExpenseData={saveRegistartionHandler} onCancel={stopRegistrationHandler}/>}
        </div>



    </div>



  );
}

export default Login;
